      FIRMI.E_FAK 
    FROM 
      FIRMI 
    WHERE 
      FIRMI.ID = NEW.FIRMA_ID 
    INTO NEW.EFAK; 
  END 
 
  IF (NEW.DOWNLOAD_DATE IS NOT NULL) THEN BEGIN 
    UPDATE OPR SET 
      OPR.IS_LOCK = 1 
    WHERE 
      OPR.ID = NEW.OPR_ID; 
  END 
END
