        OFFER_POR_NODE.OFFER_ID = NEW.OFFER_ID 
    INTO 
        :L_POR_COUNT; 
 
    L_USER_COUNT = COALESCE(L_USER_COUNT, 0); 
    L_POR_COUNT = L_POR_COUNT; 
 
    UPDATE OFFERS SET 
        STATUS = IIF (:L_USER_COUNT = 0, 3, IIF(:L_POR_COUNT < :L_USER_COUNT, 2, 3)) 
    WHERE 
        ID = NEW.OFFER_ID; 
END
